sources.styles/_variables.scss:
//colors
$purple-dark: #113250;
$purple-light: #0b335f;
$purple-lightest: #2d4e79;
$purple-pale: #fcf8ff;
$white: #ffffff;
$grey: #494c4b;
$grey-light: #5c615f;
$grey-lightest: #c2c6c7;
$red: #ce5e5e;
$green: #5ece9b;

$link-color: #0366d6;
//font
$base-font-family: "Source Sans Pro", sans-serif;
$base-font-size: 14px;

$heading-font-family: "Source Sans Pro", sans-serif;
$h1-font-size: 22px;
$h2-font-size: 20px;
$h3-font-size: 16px;
$huge-font-size: 40px;

//base-vars
$base-spacing: 8px;
$base-page-width: 1280px;

sources.styles/_layout.scss:
.dashboard {
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
}

.dashboard div.column {
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
}

.dashboard div.row {
  display: flex;
  flex: 1 100%;
  flex-flow: row wrap;
  justify-content: space-around;
  align-items: flex-start;
}

sources.widgets/investments/investments.scss:
.crypto {
  min-width: 200px;
  .return {
    font-size: 20px;
    font-weight: 600;
    margin-top: 15px;
  }
  .direction {
    font-size: 16px;
    font-weight: 600;
    padding-top: 8px;
    position: absolute;
    right: 15px;
    top: 7px;
  }
}

sources.widgets/weather/weather.scss:
.weather {
  min-width: 200px;
  .group-content {
    padding: 2px !important;
  }
  .group {
    padding-bottom: 0 !important;
  }
  .temp {
    font-size: 45px;
    font-weight: 600;
    position: absolute;
    right: 25px;
    top: 55px;
  }
}

sources.widgets/countdown/countdown.scss:
.countdown {
  min-height: 115px;
  min-width: 210px;
  .count {
    font-size: 35px;
    font-weight: 400;
    padding: 15px 0 5px 0;
    text-align: center;
  }
  .meta {
    font-size: 15px;
    color: $white;
    opacity: 0.9;
    padding-bottom: 10px;
    text-align: center;
  }
